                <ul className="map-list">
                    {locate.map((item, index) => (
                        <li className="map-list-item" key={index}>
                            <input
                                type="checkbox"
                                id={`list-${String(index)}`}
                                name="map-radio"
                                value={item}
                                checked={selectedItems.includes(item)} // 선택된 항목을 반영
                                onChange={handleCheckboxChange} // 체크박스 변경 시 호출되는 핸들러
                            />
                            <label htmlFor={`list-${String(index)}`}>
                                <span className="map-list-txt">{item}</span>
                            </label>
                        </li>
                    ))}
                </ul>
